function yoink(url){if(!yoink.loaded[url]){var req=new XMLHttpRequest;req.open("GET",url,false);req.send();var base=url.substring(0,url.lastIndexOf("/"));var relYoink=function(url){if(url[0]!="/"&&url.indexOf("://")==-1){url=base+"/"+url}return yoink(url)};var f=eval("(function (yoink) {"+req.responseText+"})");yoink.loaded[url]=f(relYoink)}return yoink.loaded[url]}yoink.loaded={};